mapper.select:

select * from
	(select n.*, row_number()
	   over(order by store_no desc) rnum
	   from store n) 
	     where rnum >= #{startNo} and rnum <= #{endNo}

mapper.update:
update store set store_title = #{store_title},
    store_cont = #{store_cont},
    	store_image = #{store_image}
    	 where store_no = #{store_no}
