mapper.select:

select * from
	(select b.*, row_number()
	   over(order by faq_no desc) rnum
	   from faq b) 
	     where rnum >= #{startNo} and rnum <= #{endNo}



			select * from
            (select b.*, row_number()
               over(order by faq_no desc) rnum
               from faq b where faq_cate=#{faq_cate}) 
                 where rnum >= #{startNo} and rnum <= #{endNo} and faq_cate=#{faq_cate}
